<template>
  <div class="mentors">
    <h1 class="subheading grey--text">Mentors</h1>
    <v-container class="my-5">
      <v-layout row wrap>
        <v-flex xs12 sm6 md4 lg3 v-for="mentor in mentors" :key="mentor.name">
          <v-card flat class="text-xs-center ma-3">
            <v-responsive class="pa-4">
              <v-avatar size="80" class="mx-auto">
                <img
                  src="data:image/jpeg;base64,"
                  alt
                  srcset
                />
              </v-avatar>
            </v-responsive>
            <v-card-text>
              <div class="subheading">{{ mentor.firstName }} {{ mentor.lastName }}</div>
              <div class="grey--text">{{ mentor.expertize }}</div>
            </v-card-text>
            <v-card-actions>
              <v-btn flat color="grey lighten-2">
                <v-icon small left color="primary lighten-1">mdi-email</v-icon>
                <span>Email</span>
              </v-btn>
            </v-card-actions>
          </v-card>
        </v-flex>
      </v-layout>
    </v-container>
  </div>
</template>
<script>
/* eslint-disable no-console */

import axios from "axios";
export default {
  data() {
    return {
      mentors: []
    };
  },
  async mounted() {
    await axios
      .get("http://localhost:3000/api/mentors")
      .then(response => {
        let newArray = response.data;
        newArray.forEach(element => {
          this.mentors.push(element);
        });
      })
      .catch();
  }
};
</script>
<style scoped></style>
